import styled from "styled-components";
import React, { useState } from "react";
import Avartar1 from "../../dummyResources/Avartar1.png";
import MainPicDummy from "../../dummyResources/MainPicDummy.jpg";
import { Collapse } from "react-collapse";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";
import "./FeedPage.css";
import { Option } from "react-dropdown";
import Avartar from "../../components/shared/Avartar";
import { colors, Container } from "../../Styles";
import Header from "../../components/shared/Header/Header";
import HeaderTextHeading from "../../components/shared/Header/HeaderTextHeading";
import HeaderTextDescription from "../../components/shared/Header/HeaderTextDescription";
import Feed from "../../components/feed/Feed";

const options: Option[] = [
  {
    value: "안암근처",
    label: "안암근처",
    className: "drop-down-option",
  },
  {
    value: "신촌근처",
    label: "신촌근처",
    className: "drop-down-option",
  },
  {
    value: "혜화근처",
    label: "혜화근처",
    className: "drop-down-option",
  },
];

interface Props {}

export default function FeedPage(props: Props) {
  return (
    <Container>
      <Top>
        <TopWrapper>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Dropdown
              controlClassName="drop-down-className"
              options={options}
              value={options[0]}
            />
          </div>
          <RequestP>식탁 건의하기</RequestP>
        </TopWrapper>
      </Top>
      <Header src={MainPicDummy}>
        <HeaderTextHeading>친구들과 놀러가요!</HeaderTextHeading>
        <HeaderTextDescription>
          취향이 통하는 대학친구들과 즐기는 공간
        </HeaderTextDescription>
      </Header>
      <Feed
        placeImgSrc={MainPicDummy}
        feedParticipant={[Avartar1, Avartar1, Avartar1, Avartar1]}
      >
        <FeedHeading>bunt24</FeedHeading>
        <FeedDetailP>#조용한 #감성적인 #분위기있는</FeedDetailP>
        <FeedTimeP>참살이 이번주 금요일 오후 1시/5시/7시/9시</FeedTimeP>
      </Feed>
      <FeedContainer>
        <FeedLeftContainer>
          <FeedAlomostFull>
            <p>곧 마감</p>
          </FeedAlomostFull>
          <FeedImg src={MainPicDummy} />
        </FeedLeftContainer>
        <FeedDescription>
