import styled from "styled-components";
import PostMainPicDummy from "../../dummyResources/PostMainPicDummy.png";
import PostSubPicDummy from "../../dummyResources/PostSubPicDummy.png";

interface Props {}

export default function PostPage(props: Props) {
  return (
    <Container>
      <Header>
        <HeaderPic src={PostMainPicDummy} />
        <HeaderText>
          <HeaderTextHeading>안암 막걸리 찬가</HeaderTextHeading>
          <HeaderTextDescription>
            내일 / 서울 용상구 보광로 60길 14-38 <br />
            20세~26세
          </HeaderTextDescription>
        </HeaderText>
      </Header>
      <ContainerDescription>
        <p>
          새내기 미팅은 여기서!! 코로나 학번 모여라~~~
          <br />
          준비물은 청춘과 체력! 같이 이런저런 이야기 하면서 놀아요 ㅎ<br />
          나이 상관 없이 즐기는 광란의 안암 프리즘!
          <br />
          좋은 추억 쌓아보아요!
          <br />
        </p>
        <DetailDescription>
          <p>가계 정보 더보기</p>
        </DetailDescription>
      </ContainerDescription>
      <gridContainer>
          <gridRow>
              <gridPic />
          </gridRow>
      </gridContainer>
    </Container>
  );
}

const Container = styled.div`
  width: 100%;
`;

const Header = styled.div`
  width: 100%;
  position: relative;
`;

const HeaderPic = styled.img`
  width: 100%;
  height: 250px;
  object-fit: cover;
`;

const HeaderText = styled.div`
  color: white;
  position: absolute;
  top: 70%;
  left: 5%;
  width: 60%;
`;

const HeaderTextHeading = styled.h3`
  padding-bottom: 11px;
  border-bottom: 1px solid white;
  font-size: 24px;
  font-weight: 700;
`;

const HeaderTextDescription = styled.p`
  margin-top: 8px;
  font-size: 12px;
  font-weight: 500;
`;

const ContainerDescription = styled.div`
  margin-top: 27px;
  width: 80%;
  margin-left: auto;
  margin-right: auto;
  size: 12px;
  line-height: 18px;
  color: #6f7789;
  position: relative;
`;

const DetailDescription = styled.div`
  position: absolute;
  bottom: -15px;
  right: 0;
`;

const gridContainer = styled.div`
  width: 80%;
  margin-left: auto;
  margin-right: auto;
`;

const gridRow = styled.div`
  width: 100%;
  justify-content: space-between;
`;

const gridPic = styled.img`
  width: 100px;
  height: 100px;

`;
