import styled from "styled-components";
import { colors } from "../../Styles";
import Avartar from "../shared/Avartar";

interface Props {
  placeImgSrc: string;
  feedClosed?: boolean;
  feedTag?: string;
  feedParticipant: string[];
  children: React.ReactNode;
}

export default function Feed(props: Props) {
  const tag: string | null | undefined = props.feedTag;

  return (
    <FeedContainer>
      <FeedLeftContainer>
        {props.feedClosed ? (
          <FeedFull>
            <p>곧 마감</p>
          </FeedFull>
        ) : null}
        {props.feedTag ? (
          <FeedTag>
            <p>{props.feedTag}</p>
          </FeedTag>
        ) : null}
        <FeedImg src={props.placeImgSrc} />
      </FeedLeftContainer>
      <FeedDescription>
        {props.children}
        <AvartarContainer>
          {props.feedParticipant.map((item, idx) => {
            if (idx < 4) {
              return <Avartar src={item} marginRight={"-5px"} />;
            }
          })}

          if(props.feedpa)
        </AvartarContainer>
      </FeedDescription>
    </FeedContainer>
  );
}

const FeedContainer = styled.div`
  width: 90%;
  display: flex;
  justify-content: center;
  margin-top: 15px;
`;
const FeedLeftContainer = styled.div`
  width: 100px;
  height: 100px;
  padding: 0px;
  position: relative;
`;
const FeedImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 10%;
  object-fit: cover;
`;

const FeedDescription = styled.div`
  width: 200px;
  padding-left: 10px;
  padding-top: 5px;
`;

const FeedTag = styled.div`
  position: absolute;
  background-color: ${colors.MidBlue};
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 5px;
  height: 18px;
  padding: 0px 5px 0px 5px;
  top: -5px;
  transform: translateX(-8px);

  p {
    color: white;
    font-weight: 700;
    font-size: 11px;
  }
`;

const FeedFull = styled.div`
  position: absolute;
  background-color: gray;
  width: 100%;
  height: 100%;
  opacity: 0.5;
  border-radius: 15%;
  justify-content: center;
  display: flex;
  align-items: center;
  p {
    color: white;
    font-weight: 800;
  }
`;

const AvartarContainer = styled.div`
  display: flex;
`;
