import styled, { createGlobalStyle } from "styled-components";
import reset from "styled-reset";

interface Colors {
  StrongBlue: string;
  MidBlue: string;
  LightBlue: string;
  BareBlue: string;
  Black: string;
  MidGray: string;
  LightGray: string;
  BareGray: string;
}

export const GlobalStyles = createGlobalStyle`
    ${reset}
   p, a, span, h3, h4, div {
     font-family: "Spoqa Han Sans Neo", "Spoqa Han Sans JP", sans-serif;
     color: "#12121D";
   }
`;

export const colors: Colors = {
  StrongBlue: "#1C43B7",
  MidBlue: "#18A0FB",
  LightBlue: "#DBEDFF",
  BareBlue: "#F8FAFD",
  Black: "#12121D",
  MidGray: "#6F7789",
  LightGray: "#A7B0C0",
  BareGray: "#C4CBD8",
};

export const AppContainer = styled.div`
  width: 375px;
  min-height: 100vh;
  display: block;
  margin-left: auto;
  margin-right: auto;
  background-color: white;
`;

export const Container = styled.div`
  width: 100%;
  position: relative;
`;

export const ProcedureHeading = styled.div`
  font-weight: bold;
  font-size: 26px;
  display: flex;
  align-items: center;
`;

export const MainBtn = styled.div`
  width: 300px;
  margin-left: auto;
  margin-right: auto;
  height: 50px;
  background-color: #18a0fb;
  border-radius: 5px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 30px;
  filter: drop-shadow(16px 16px 20px rgba(75, 88, 208, 0.5));
  color: white;
  p {
    color: white;
  }
`;

export const SelectionBox = styled.div`
  width: 90%;
  margin-left: auto;
  margin-right: auto;
  height: 90px;
  background-color: white;
  margin-top: 30px;
  display: flex;
  /* justify-content: center; */
  align-items: center;
  filter: drop-shadow(16px 16px 20px rgba(0, 0, 0, 0.15));
  border-radius: 10px;
`;

export const SelectionMainText = styled.p`
  color: "#12121D";
  font-weight: 500;
  font-size: 14px;
`;

export const SelectionSubText = styled.p`
  color: "#A7B0C0";
  font-weight: 400;
  font-size: 13px;
`;

export const SpaceForNavBar = styled.div`
  width: 100%;
  height: 100px;
`;

export const BottomNavBarContainer = styled.div`
  height: 75px;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  left: 50;
  bottom: 0;
  background-color: white;
  filter: drop-shadow(16px 16px 20px rgba(75, 88, 208, 0.15));
`;

export const BottomFixedButtonContainer = styled(BottomNavBarContainer)`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;
