import styled, { createGlobalStyle } from "styled-components";
import reset from "styled-reset";

interface colors {
  StrongBlue: string;
  MidBlue: string;
  LightBlue: string;
  BareBlue: string;
  Black: string;
  MidGray: string;
  LightGray: string;
  BareGray: string;
}

export const GlobalStyles = createGlobalStyle`
    ${reset}
   p, a, span {
     font-family: "Spoqa Han Sans Neo", "Spoqa Han Sans JP", sans-serif;
   }
`;

export const AppContainer = styled.div`
  width: 375px;
  min-height: 100vh;
  display: block;
  margin-left: auto;
  margin-right: auto;
  border-left: 1px solid ${colors.BareGray};
  border-right: 1px solid ${colors.BareGray$};
  background-color: ${colors.BareBlue};
`;
